import { useState } from "react";
import { Utils } from "../../shared/Utils";
import "./Calculator.css";
import DisplayPanel from "./DisplayPanel/DisplayPanel";
import InputPanel from "./InputPanel/InputPanel";

const Calculator: React.FC = () => {
  const [result, setResult] = useState("");
  const [input, setInput] = useState("0");

  const handleClick = (value: string) => {
    if (Utils.isCancel(value)) {
      setInput("0");
      setResult("");
    } else if (Utils.isNumber(value)) {
      setInput((prev) =>
        prev.length === 1 && prev === "0" ? value : prev + value
      );
    } else if (
      Utils.isDecimal(value)
    ) {
      const hasNoOperatorOrDecimal = (!Utils.MATH_OPERATORS.some((x) => input.includes(x)) || !input.includes("."));

      if (hasNoOperatorOrDecimal) {
        setInput((prev) => prev + value);
        return;
      }

      const lastIndexOfOperator = Math.max(...Utils.MATH_OPERATORS.map(x => input.lastIndexOf(x)));
      const lastIndexOfDecimal = input.lastIndexOf(".");
      if ((lastIndexOfDecimal < lastIndexOfOperator)) {
        setInput((prev) => prev + value);
        return;
      }
    } else if (Utils.isMathOperator(value)) {
      setInput((prevValue) => {
        const isPreviousAlsoMathOperator = Utils.isMathOperator(
          prevValue[prevValue.length - 1]
        );

        const isSubtractOperator = value === "-";
        if (isPreviousAlsoMathOperator && !isSubtractOperator) {
          const newInput = prevValue.substring(0, prevValue.length - 1) + value;
          return newInput;
        } else {
          return prevValue + value;
        }
      });
    } else if (Utils.isEqualOperator(value)) {
      let validInput = input;
      const isLastInputIsMathOperator = Utils.isMathOperator(
        input[input.length - 1]
      );
      
      if (isLastInputIsMathOperator) {
        validInput = input.substring(0, input.length - 1);
        setInput(validInput);
      }
      setResult(validInput + "=" + Utils.calculate(validInput));
    }
  };

  return (
    <div className="calculator">
      <DisplayPanel input={input} result={result} />
      <InputPanel onClick={handleClick} />
    </div>
  );
};

export default Calculator;
