import { CSSProperties } from "react";
import "./InputButton.css";

const InputButton: React.FC<InputButtonProps> = (props) => (
  <div style={props.style} id={props.id} className={['input-button', 'bg-'+props.bgColor, 'font-'+props.color].join(' ')} onClick={() => props.onClick!(props.value!)}>
    {props.text}
  </div>
);

InputButton.defaultProps = {
    bgColor: 'black',
    onClick: () => {},
    color: 'white',
    text: '0',
    id: 'one'
}

export interface InputButtonProps {
  text?: string;
  value?: string;
  color?: string;
  bgColor?: string;
  id?: string;
  style?: CSSProperties | undefined,
  onClick?: (value: string) => void;
}

export default InputButton;
