import { useState } from 'react';
import './Calculator.css';
import DisplayPanel from './DisplayPanel/DisplayPanel';
import InputPanel from './InputPanel/InputPanel';

const Calculator: React.FC = () => {
    const [resultHistory, setResultHistory] = useState(0);
    const [result, setResult] = useState('');
    const [input, setInput] = useState('0');

    const handleClick = (value: string) => {
        if (isDecimal(value) && input.includes('.') || result.includes('.')) {
            return;
        }

        if (isNumber(value) || isDecimal(value) && isNumber(input)) {
            setInput(prev => prev + value);
        } else if (input === '0') {
            setInput(value);
        } else if (isMathOperator(value)) {
            const ans = applyOperator(value);
        } else if (isEqualOperator(value)) {
            const ans = calculate()
            console.log(ans);
            setResult(prev => `${prev}=${ans}`);
        } else if (isCancel(value)) {
            setInput('0');
            setResult('');
        }
    }

    const isNumber = (value: string) => !Number.isNaN(value);
    const isDecimal = (value: string) => value === '.';
    const isMathOperator = (value: string) => ['+', '-', '/', '*'].includes(value);
    const isEqualOperator = (value: string) => value === '=';
    const isCancel = (value: string) => value === 'AC';

    const calculate = () => {
        return eval(result);
    }

    const applyOperator = (operator: string) => {
        if (isNumber(result)) {
            setResult(prev => `${result}${operator}`);
        }
    }

    return <div className='calculator'>
        <DisplayPanel input={input} result={result} />
        <InputPanel onClick={handleClick} />
    </div>
}

export default Calculator;
