import "./InputButton.css";

const InputButton: React.FC<InputButtonProps> = (props) => (
  <div className={['input-button', 'bg-'+props.bgColor, 'font-'+props.color, `grid-${props.row}-${props.col}`].join(' ')} onClick={() => props.onClick!(props.value!)}>
    {props.text}
  </div>
);

InputButton.defaultProps = {
    bgColor: 'black',
    onClick: () => {},
    color: 'white',
    row: 1,
    col: 1,
    text: '0'
}

export interface InputButtonProps {
  text?: string;
  value?: string;
  color?: string;
  bgColor?: string;
  row?: number;
  col?: number;
  onClick?: (value: string) => void;
}

export default InputButton;
